create table post_comments
(
    id                bigint unsigned auto_increment
        primary key,
    post_id           bigint unsigned           not null,
    parent_comment_id bigint unsigned default 0 not null comment '父评论id，如果为0则表示没有父级',
    to_uid            bigint unsigned default 0 not null,
    from_uid          bigint unsigned           not null,
    content           text                      not null comment '评论内容',
    likes             bigint unsigned default 0 not null comment '点赞数',
    create_time       datetime                  not null,
    update_time       datetime                  not null on update CURRENT_TIMESTAMP,
    deleted           tinyint(2)                not null
)
    charset = utf8mb4;

create index idx_parent_comment_id
    on post_comments (parent_comment_id);

create index idx_postid_parentcommentid
    on post_comments (post_id, parent_comment_id);

create index idx_to_uid
    on post_comments (to_uid);

