create table teams
(
    id          bigint unsigned auto_increment
        primary key,
    name        text                    not null,
    logo        varchar(255) default '' null,
    description text                    null comment '团队描述，关于团队表，暂时不额外附加经历表了，经历也都写在这',
    tag_ids     varchar(50)  default '' null,
    status      tinyint(2)              not null comment '0-待发布（不招人） 1-发布中（招人），只有发布中状态的团队才会展示在首页',
    deleted     tinyint(2)   default 0  not null,
    create_time datetime                not null,
    update_time datetime                not null on update CURRENT_TIMESTAMP
)
    charset = utf8mb4;

create fulltext index name_text
    on teams (name);

