create table user_info
(
    id                        bigint unsigned auto_increment
        primary key,
    uid                       bigint unsigned         not null,
    school_id                 int                     null,
    degree_id                 int                     null,
    area_id                   int                     null,
    role                      tinyint(2)              null comment 'Role of user application',
    grade                     int                     null comment 'If he is a grade 17 student, fill in 17',
    subject                   varchar(20)  default '' not null comment 'For example: Information Engineering, software engineering and other disciplines',
    real_name                 varchar(20)  default '' not null,
    credentials_photo_front   varchar(255) default '' not null comment 'photo url',
    credentials_photo_reverse varchar(255) default '' not null comment 'photo url',
    tag_ids                   varchar(50)  default '' not null,
    reason                    varchar(50)  default '' not null comment '申请不通过时填写地原因',
    status                    tinyint(2)              not null,
    deleted                   tinyint(2)              not null,
    create_time               datetime                not null,
    update_time               datetime                not null on update CURRENT_TIMESTAMP
)
    charset = utf8mb4;

create index idx_uid
    on user_info (uid);

create index role
    on user_info (role);

