mapper.select:
        select u.uid uid, nickname, real_name, avatar, description, degree_id, grade, school_id, tag_ids
        from (`user` u, user_tags ut)
	      inner join user_info ui on u.uid = ui.uid

            1=1

                and ui.grade = #{grade}


                and ut.uid = u.uid and ut.tag_id = #{tagId}


                and ui.school_id = #{schoolId}


                and ui.degree_id = #{degreeId}


        and u.deleted = 0
        and ui.role = 3
        and ui.status = 1
        group by u.uid
        order by ui.create_time desc
